import React, { useState, useEffect } from 'react';
import Navbar from '../components/navbar/Navbar';
import Foter from '../components/footer/Foter';
import './Contact.css';

const Contact = () => {
  const [email, setEmail] = useState('');
  const [question, setQuestion] = useState('');
  const [description, setDescription] = useState('');

  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(String(email).toLowerCase());
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateEmail(email)) {
      alert('It is not a valid email');
      return;
    }
    // Handle form submission logic here
    console.log({ email, question, description });
    window.location.reload();
  };

  useEffect(() => {
    const handleMouseMove = (e) => {
      const line = document.getElementById('golden-line');
      line.style.left = `${e.clientX}px`;
      line.style.top = `${e.clientY}px`;
    };

    document.addEventListener('mousemove', handleMouseMove);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <div>
      <Navbar />
      <div className="contact-container">
        <h2>Contact Us</h2>
        <form onSubmit={handleSubmit} className="contact-form">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />

          <label htmlFor="question">Question:</label>
          <select
            id="question"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            required
          >
            <option value="">Select a question</option>
            <option value="question1">Question 1</option>
            <option value="question2">Question 2</option>
            <option value="question3">Question 3</option>
            <option value="question4">Question 4</option>
            <option value="question5">Question 5</option>
            <option value="question6">Question 6</option>
            <option value="question7">Question 7</option>
            <option value="question8">Question 8</option>
            <option value="question9">Question 9</option>
            <option value="question10">Question 10</option>
          </select>

          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          ></textarea>

          <button type="submit">Submit</button>
        </form>
      </div>
      <Foter />
    </div>
  );
};

export default Contact;
