import React from 'react'
import { useEffect, useState } from 'react'
import CocktailItem from './cocktailItem/CocktailItem'

const Main = () => {
    const[cocktail, setCocktail]= useState([])
    useEffect(() => {
        let url = `https://cocktails.solvro.pl/api/v1/cocktails`
        fetch(url).then(response=>response.json()).then(data=>setCocktail(data.id)).catch(error=>console.error(error))
    },[])
  return (
    <div>
          <div>
        <h2 className="text-center">Our <span className='badge bg-danger'>Cocktails</span></h2>
    {cocktail.map((name, id)=>{
        return <CocktailItem key={id}
        title={cocktail.name}
        description={cocktail.instructions}
        src={cocktail.imageUrl}
        />
    })}
    </div>
    </div>
  )
}

export default Main
