import React from 'react'
import { useEffect, useState } from 'react'
const[articles,setArticles]= useState([])
    useEffect(() => {
        let url = `https://cocktails.solvro.pl/api/v1/cocktails`
        fetch(url).then(response=>response.json()).then(data=>setCoctail(data.cocktails)).catch(error=>console.error(error))
    },[])
const Main = () => {
  return (
    <div>
          <div>
        <h2 className="text-center">Latest <span className='badge bg-danger'>News</span></h2>
    {articles.map((news, index)=>{
        return <NewsItem key={index}
        title={news.title}
        description={news.description}
        src={news.urlToImage}
        url={news.url}
        />
    })}
    </div>
    </div>
  )
}

export default Main
