// filepath: c:\Users\mwasz\OneDrive\Desktop\Solvro Cocktails\cocktail\src\components\cocktailItem\CocktailItem.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import './cocktailitem.css';

const CocktailItem = ({ id, title, description, src }) => {
    const navigate = useNavigate();

    const handleReadMore = () => {
        navigate(`/item/${id}`);
    };

    return (
        <div className="cocktail-item">
            <img src={src} alt={title} className="cocktail-image" />
            <h3>{title}</h3>
            <p>{description}</p>
            <button onClick={handleReadMore} className="read-more-button">Read More</button>
        </div>
    );
};

export default CocktailItem;
