import React, { useEffect, useState } from 'react';
import './explore.css';

const Explore = () => {
    const [menuList, setMenuList] = useState([]);
    const [category, setCategory] = useState('all');

    useEffect(() => {
        const url = `https://cocktails.solvro.pl/api/v1/cocktails`;
        fetch(url)
            .then(response => response.json())
            .then(data => {
                if (Array.isArray(data.data)) {
                    setMenuList(data.data.filter(item => item.imageUrl)); // Filter items with images
                } else {
                    console.error('API response is not an array:', data);
                }
            })
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    const categories = Array.from(new Set(menuList.map(item => item.category)));

    const filteredMenuList = category === 'all' ? menuList : menuList.filter(item => item.category === category);

    return (
        <div className='explore-menu' id='explore-menu'>
            <h1>Explore Our Menu</h1>
            <p className='explore-menu-text'>Choose from a diverse menu to end your cravings</p>
            <div className="category-buttons">
                <button onClick={() => setCategory('all')} className={category === 'all' ? 'active' : ''}>
                    <img src={`../../assets/all.jpg`} alt="All" className="category-image" />
                </button>
                {categories.map((cat, index) => (
                    <button key={index} onClick={() => setCategory(cat)} className={category === cat ? 'active' : ''}>
                        <img src={`../../assets/${cat.toLowerCase().replace(/ /g, '-')}.jpg`} alt={cat} className="category-image" />
                    </button>
                ))}
            </div>
            <div className="explore-menu-list">
                {filteredMenuList.map((item, index) => (
                    <div key={index} className="explore-menu-list-item">
                        <img className={category === item.name ? "active" : ""} src={item.imageUrl} alt={item.name} />
                        <p>{item.name}</p>
                    </div>
                ))}
            </div>
            <hr />
        </div>
    );
};

export default Explore;
