import React, { useEffect, useState } from 'react'
import Navbar from '../components/navbar/Navbar'
import Footer from '../components/footer/Foter'
import './BestSellers.css'

const BestSellers = () => {
  const [cocktails, setCocktails] = useState([]);

  useEffect(() => {
    const url = `https://cocktails.solvro.pl/api/v1/cocktails?perPage=133`;
    fetch(url)
      .then(response => response.json())
      .then(data => {
        if (Array.isArray(data.data)) {
          // Randomly select ten cocktails
          const shuffled = data.data.sort(() => 0.5 - Math.random());
          const selectedCocktails = shuffled.slice(0, 10);
          setCocktails(selectedCocktails);
        } else {
          console.error('API response is not an array:', data);
        }
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  return (
    <div>
      <Navbar />
      <div className="best-sellers">
        <h1>Best Sellers</h1>
        <div className="cocktail-list">
          {cocktails.map(cocktail => (
            <div key={cocktail.id} className="card cocktail-item">
              <img src={cocktail.imageUrl} alt={cocktail.name} className="card-img-top" />
              <div className="card-body">
                <h2 className="card-title">{cocktail.name}</h2>
              </div>
            </div>
          ))}
        </div>
      </div>
      <Footer />
    </div>
  )
}

export default BestSellers
